import { useNavigate } from "react-router-dom";
import axios from "axios";
import Swal from "sweetalert2";
import { useState } from "react";

function register() {
  const navigate = useNavigate();

  // Determining the fields required
  const [registerInput, setRegister] = useState({
    name: '',
    email: '',
    password: '',
    error_list: [],
  });

  const handleInput = (e) => {
    e.persist();
    setRegister({ ...registerInput, [e.target.name]: e.target.value });
  };

  const submitRegister = (e) => {
    e.preventDefault();

    // Get CSRF token
    axios.get('/sanctum/csrf-cookie').then(response => {
      // Proceed with the POST request
      const data = {
        name: registerInput.name,
        email: registerInput.email,
        password: registerInput.password,
      };

      axios.post(`/api/register`, data).then(res => {
        if (res.data.status === 200) {
          localStorage.setItem('auth_token', res.data.token);
          localStorage.setItem('auth_name', res.data.username);
          Swal.fire("Success", res.data.message, "success");
          navigate('/');
        } else {
          setRegister({ ...registerInput, error_list: res.data.validation_errors });
        }
      }).catch(error => {
        console.error('Registration request failed:', error);
      });
    }).catch(error => {
      console.error('CSRF token request failed:', error);
    });
  };

  return (
    <div className="registration">
      <div className="registrationback"></div>
      <div className="registrationcontent">
        <div className="regheader">
          <h2>Register</h2>
          <p className="belowregname">Create an account</p>
        </div>
        <form onSubmit={submitRegister}>
          <div className='card-header'>
            <h4>Register</h4>
          </div>
          <div className='card-body'>
            <div className="form-group mb-3">
              <label>Full Name</label>
              <input type="text" name="name" onChange={handleInput} value={registerInput.name} className="form-control" />
              <span>{registerInput.error_list.name}</span>
            </div>
            <div className="form-group mb-3">
              <label>Email address</label>
              <input type="email" name="email" onChange={handleInput} value={registerInput.email} className="form-control" />
              <span>{registerInput.error_list.email}</span>
            </div>
            <div className="form-group mb-3">
              <label>Password</label>
              <input type="password" name="password" onChange={handleInput} value={registerInput.password} className="form-control" />
              <span>{registerInput.error_list.password}</span>
            </div>
            <div className="mb-3 form-check">
              <input type="checkbox" className="form-check-input" />
              <label className="form-check-label">Check me out</label>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
          </div>
        </form>
      </div>
    </div>
  );

}

export default register;
