import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import axios from 'axios';
import Header from "./header";
import Footer from "./footer";

export default function AddProduct() {
  const navigate = useNavigate();

  const initialFormData = {
    product: "",
    manufacturer: "",
    price: "",
    quantity: "",
    tags: "",
    pdimg: "",
    description: "",
    user_id: "",
  };

  const [formData, setFormData] = useState(initialFormData);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit, setIsSubmit] = useState(false);

  useEffect(() => {
    // Fetch CSRF token
    async function fetchCsrfToken() {
      try {
        await axios.get("http://127.0.0.1:8000/sanctum/csrf-cookie");
      } catch (error) {
        console.error('Error fetching CSRF token:', error);
      }
    }

    fetchCsrfToken();
  }, []);

  function handleChange(event) {
    const vendor = JSON.parse(localStorage.getItem("vendor-info"));
    const { name, value, type, files } = event.target;
    setFormData((prevValue) => ({
      ...prevValue,
      [name]: type === "file" ? files[0] : value,
      user_id: vendor.id,
    }));
  }

  async function handleSubmit(event) {
    event.preventDefault();

    try {
      // Set CSRF token in the headers
      axios.defaults.withCredentials = true;

      const response = await axios.post("http://127.0.0.1:8000/api/addproduct", formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      alert("Product Saved");
      navigate("/myproducts");
    } catch (error) {
      console.error('Error adding product:', error);
    }
  }

  function validate(values) {
    const errors = {};
    if (!values.product) {
      errors.product = "Enter the product's name";
    }
    // ... add validations for other fields

    return errors;
  }

  useEffect(() => {
    if (Object.keys(formErrors).length === 0 && isSubmit) {
      handleSubmit();
    }
  }, [formErrors, isSubmit]);

  return (
    <div>
      <Header />
      <>
        {localStorage.getItem("vendor-info") ? (
          <div className="formbody">
            <div className="addproductheader">
            <div className="togoback">
              <a href="/myproducts">Go Back</a>
            </div>
          </div>
            <div className="formdiv">
              <form onSubmit={handleSubmit}>
              <div className="addpdheader">Add New Product</div>
              <div>
                  <label for="product">Product Name</label>
                  <input
                    type="text"
                    onChange={handleChange}
                    name="product"
                    style={{ marginBottom: "0px" }}
                  />
                  <p
                    classname="errorsmsg"
                    style={{ color: "red", fontSize: "17px", marginTop: "0px" }}
                  >
                    {formErrors.product}
                  </p>
                </div>

                <div>
                  <label for="title">Manufacturer</label>
                  <input
                    type="text"
                    name="manufacturer"
                    placeholder="Example: Audi"
                    onChange={handleChange}
                    style={{ marginBottom: "0px" }}
                  />
                  <p
                    classname="errorsmsg"
                    style={{ color: "red", fontSize: "17px", marginTop: "0px" }}
                  >
                    {formErrors.manufacturer}
                  </p>
                </div>

                <div>
                  <label for="price">Price</label>
                  <input
                    type="text"
                    style={{ marginBottom: "0px" }}
                    onChange={handleChange}
                    name="price"
                    placeholder="In USD."
                  />
                  <p
                    classname="errorsmsg"
                    style={{ color: "red", fontSize: "17px", marginTop: "0px" }}
                  >
                    {formErrors.price}
                  </p>
                </div>

                <div>
                  <label for="quantity">Quantity</label>
                  <input
                    type="text"
                    style={{ marginBottom: "0px" }}
                    onChange={handleChange}
                    name="quantity"
                  />
                  <p
                    classname="errorsmsg"
                    style={{ color: "red", fontSize: "17px", marginTop: "0px" }}
                  >
                    {formErrors.quantity}
                  </p>
                </div>

                <div>
                  <label for="tags">Tags (Comma Separated)</label>
                  <input
                    type="text"
                    name="tags"
                    style={{ marginBottom: "0px" }}
                    onChange={handleChange}
                    placeholder="Example: Camera, Lens, Photograph etc"
                  />
                  <p
                    classname="errorsmsg"
                    style={{ color: "red", fontSize: "17px", marginTop: "0px" }}
                  >
                    {formErrors.tags}
                  </p>
                </div>

                <div className="imagepart">
                  <label for="productimage">Product Image: </label>
                  <input
                    type="file"
                    style={{ marginBottom: "0px" }}
                    onChange={handleChange}
                    name="pdimg"
                  />
                  <p
                    classname="errorsmsg"
                    style={{ color: "red", fontSize: "17px", marginTop: "0px" }}
                  >
                    {formErrors.pdimg}
                  </p>
                </div>

                <div>
                  <label for="description">Product Description</label>
                  <textarea
                    name="description"
                    rows="10"
                    onChange={handleChange}
                    style={{ marginBottom: "0px" }}
                    placeholder="Include product's details"
                  ></textarea>
                  <p
                    classname="errorsmsg"
                    style={{ color: "red", fontSize: "17px", marginTop: "0px" }}
                  >
                    {formErrors.description}
                  </p>
                </div>
                <button>Add Product</button>
              </form>
            </div>
          </div>
        ) : (
          <div>
            {navigate("/login")} {alert("Please Login First")}
          </div>
        )}
      </>
      <Footer />
    </div>
  );
}
