import axios from 'axios';
import React, { useState } from 'react';
import { useNavigate } from "react-router-dom";
//import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';
import Swal from 'sweetalert2';
//import Navbar from '../../../layouts/frontend/Navbar';

    const Login = () => {
        const navigate = useNavigate();
        const [loginInput, setLogin] = useState({
            email: '',
            password: '',
        });

    const handleInput = (e) => {
        // e.persist();
        setLogin({...loginInput, [e.target.name]: e.target.value})
    } 
    const submitLogin = async (e) => {
        e.preventDefault();

        const data = {
            email: loginInput.email,
            password: loginInput.password,

        }
        try {
          // Attempt to get CSRF cookie
          await axios.get('http://localhost:8000/sanctum/csrf-cookie');

          // Proceed with the login request
          const res = await axios.post(`http://localhost:8000/api/login`, data);

          if (res.data.status === 200) {
            // Successful login
            localStorage.setItem('auth_token', res.data.token);
            localStorage.setItem('auth_name', res.data.username);
            Swal.fire("Success", res.data.message, "success");
            navigate("/");
          } else if (res.data.status === 401) {
            // Unauthorized status, handle accordingly
            Swal.fire("Warning", res.data.message, "warning");
          } else {
            // Handle unexpected response
            console.error("Unexpected response from login request", res);
          }
        } catch (error) {
          // Handle any errors that occurred during the requests
          console.error("Error during login request", error);
        }

    }
    return(
            <div>
                {/* <Header/> */}
                <form onSubmit={submitLogin}>
                <div className='card-header'>
                    <h4>Login</h4>
                </div>
                <div className='card-body'>   
        <div className="form-group mb-3">
            <label >Email address</label>
            <input type="email" name="email" onChange={handleInput} value={loginInput.email} className="form-control"/>
            {/* <span>{loginInput.error_list.email}</span> */}
        </div>
        <div className="form-group mb-3">
            <label>Password</label>
            <input type="password" name="password" onChange={handleInput} value={loginInput.password} className="form-control" />
            {/* <span>{loginInput.error_list.password}</span> */}
        </div>
        {/* <div className="mb-3 form-check">
            <input type="checkbox" className="form-check-input"/>
            <label>Check me out</label>
        </div> */}
        <button type="submit" className="btn btn-primary">Submit</button>
        </div>
        </form>
            </div>
    );
}
export default Login;
