import { useEffect, useState } from "react";
import Header from "./header";
import Footer from "./footer";
import { useParams, useNavigate } from "react-router-dom";

export default function update() {
  const navigate = useNavigate();
  const params = useParams();
  const id = params.id;

  const [productData, setProductData] = useState([]);
  const [product, setProduct] = useState("");
  const [manufacturer, setManufacturer] = useState("");
  const [price, setPrice] = useState("");
  const [quantity, setQuantity] = useState("");
  const [tags, setTags] = useState("");
  const [pdimg, setPdimg] = useState("");
  const [description, setDescription] = useState("");

  useEffect(() => {
    fetchProductData();
  }, [id]);

  const fetchProductData = () => {
    fetch("http://127.0.0.1:8000/api/getproduct/" + id)
      .then((response) => response.json())
      .then((data) => {
        setProductData(data);
        setProduct(data.product);
        setManufacturer(data.manufacturer);
        setPrice(data.price);
        setQuantity(data.quantity);
        setTags(data.tags);
        setDescription(data.description);
        setPdimg(data.pdimg);
      });
  };

  const handleChanges = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append("product", product);
    formData.append("manufacturer", manufacturer);
    formData.append("price", price);
    formData.append("quantity", quantity);
    formData.append("tags", tags);
    formData.append("pdimg", pdimg);
    formData.append("description", description);

    let result = await fetch(
      "http://127.0.0.1:8000/api/updateproduct/" + id + "?_method=PUT",
      {
        method: "POST",
        body: formData,
      }
    );
    alert("Product Updated");
    navigate("/myproducts");
  };

  if (!localStorage.getItem("user")) {
    useEffect(() => {
      navigate("/login");
    }, [navigate]);
    return null;
  }

  return (
    <div>
      <Header />
      <div className="formbody">
        <div className="addproductheader">
          <div className="togoback">
            <a href="/myproducts">Go Back</a>
          </div>
        </div>
        <div className="formdiv">
          <form onSubmit={handleChanges}>
            {/* ... (rest of the form) */}
            <label>Product Name</label>
            <input
              type="text"
              name="product"
              value={product}
              onChange={(e) => setProduct(e.target.value)}
            />
            <label>Manufacturer</label>
            <input
              type="text"
              name="manufacturer"
              value={manufacturer}
              onChange={(e) => setManufacturer(e.target.value)}
            />
            <label>Price</label>
            <input
              type="text"
              name="price"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />
            <label>Quantity</label>
            <input
              type="text"
              name="quantity"
              value={quantity}
              onChange={(e) => setQuantity(e.target.value)}
            />
            <label>Tags</label>
            <input
              type="text"
              name="tags"
              value={tags}
              onChange={(e) => setTags(e.target.value)}
            />
            <label>Description</label>
            <input
              type="text"
              name="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <label>Product Image</label>
            <input
              type="file"
              name="pdimg"
              onChange={(e) => setPdimg(e.target.files[0])}
            />
            <img src={"http://127.0.0.1:8000/" + productData.pdimg} />
            <br />

            <button>Update Product</button>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
}
