import React, { useState } from "react";

export default function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [formErrors, setFormErrors] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const validateForm = () => {
    const errors = {};
    if (!formData.name) {
      errors.name = "Name is required";
    }
    if (!formData.email) {
      errors.email = "Email is required";
    }
    if (!formData.message) {
      errors.message = "Message is required";
    }
    setFormErrors(errors);
    return Object.keys(errors).length === 0; // Return true if there are no errors
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // Form is valid, you can submit the data or perform other actions
      alert("Your message has been sent");
      console.log(formData);
    } else {
      // Form is not valid, you can display an error message or take other actions
    }
  };

  return (
    <div className="contact">
      <div className="contact-header">
        <div className="contact-title">Contact Us</div>
      </div>
      <div className="contact-body">
            <div className="contact-body-left">
            <div className="contact-body-left-title">Contact Info</div>
            <div className="contact-body-left-info">
                <div className="contact-body-left-info-item">
                <i class="fa-solid fa-envelope"></i>
                <div>
                    <div className="contact-body-left-info-item-title">
                    Email
                    </div>
                    <div className="contact-body-left-info-item-value">

                    <a href="mailto:    ">    </a>  </div>
                </div>
                </div>
                <div className="contact-body-left-info-item">
                <i class="fa-solid fa-phone"></i>
                <div>
                    <div className="contact-body-left-info-item-title">
                    Phone
                    </div>
                    <div className="contact-body-left-info-item-value">
                    <a href="tel:    ">    </a>  </div>
                </div>
                </div>
                <div className="contact-body-left-info-item">
                <i class="fa-solid fa-location"></i>
                <div>
                    <div className="contact-body-left-info-item-title">
                    Address
                    </div>
                    <div className="contact-body-left-info-item-value">
                    <a href="https://goo.gl/maps/    " target="_blank">
                        {" "}
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />
                    </a>{" "}
                    </div>
                </div>
                </div>
            </div>
            </div>
            <div className="contact-body-right">
          <div className="contact-body-right-title">Send Us a Message</div>
          <div className="contact-body-right-form">
            <form onSubmit={handleSubmit}>
              <div className="contact-body-right-form-item">
                <input
                  type="text"
                  placeholder="Name"
                  name="name"
                  value={formData.name}
                  onChange={handleInputChange}
                />
                {formErrors.name && (
                  <span className="text-danger" role="alert">
                    {formErrors.name}
                  </span>
                )}
              </div>
              <div className="contact-body-right-form-item">
                <input
                  type="email"
                  placeholder="Email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                />
                {formErrors.email && (
                  <span className="text-danger" role="alert">
                    {formErrors.email}
                  </span>
                )}
              </div>
              <div className="contact-body-right-form-item">
                <textarea
                  placeholder="Message"
                  name="message"
                  value={formData.message}
                  onChange={handleInputChange}
                />
                {formErrors.message && (
                  <span className="text-danger" role="alert">
                    {formErrors.message}
                  </span>
                )}
              </div>
              <div className="contact-body-right-form-item">
                <button type="submit">Send</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
