import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import SubTotal from "./subtotal.js";
import { useCart } from "./cartcontext.js";

export default function Cart() {
  const navigate = useNavigate();
  const [cart, setCart] = useCart();

  const totalPrice = () => {
    try {
      let total = 0;
      cart?.map((item) => {
        total = parseInt(total) + parseInt(item.price);
      });
      return total.toLocaleString("en-US", {
        style: "currency",
        currency: "USD",
      });
    } catch (error) {
      console.log(error);
    }
  };

  const removeItem = (id) => {
    try {
      let myCart = [...cart];
      let index = myCart.findIndex((item) => item.id === id);
      myCart.splice(index, 1);
      setCart(myCart);
      localStorage.setItem("cart_info", JSON.stringify(myCart));
    } catch (error) {
      console.log(error);
    }
  };

  function getTotal(data, id) {
    console.log("Price is: " + data + " Id is: " + id);
  }

  return (
    <div>
      {localStorage.getItem("user") ? (
        <div>

          <div>
            {cart?.length > 0 ? (
              <div className="product-body">
                <div className="mypdheader">
                  <p>My Cart</p>
                </div>
                <div className="mycardcard">
                  {cart.map((item) => (
                    <div key={item.id} className="product-list">
                      <div className="mypdlist">
                        <div className="mypdimagediv">
                          <img
                            src={"http://127.0.0.1:8000/" + item.pdimg}
                            className="mypdimage"
                            alt="Logo"
                          />
                        </div>
                        <div className="mypddetails">
                          <h2>{item.product}</h2>
                          <div className="mycartdetails">
                            <div>
                              <p>Manufacturer: {item.manufacturer}</p>
                              <p>Price: {item.price}</p>
                              <p>Quantity: {item.quantity}</p>
                            </div>
                            <div>
                              <p>Tags: {item.tags}</p>
                              <p>Description: {item.description}</p>
                            </div>
                          </div>
                        </div>
                        <SubTotal
                          data={item.price}
                          getTotal={getTotal}
                          id={item.id}
                        />
                        <button
                          className="removebutton"
                          onClick={() => removeItem(item.id)}
                        >
                          Remove
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
                <div className="grandtotalcart">
                  Grand Total: {totalPrice()}
                </div>
                <div className="checkoutbutton">
                  <button>Check-out</button>
                </div>
              </div>
            ) : null}
          </div>
        </div>
      ) : (
        navigate("/login")
      )}

    </div>
  );
}
