import React from 'react';

const Dashboard = () => {

    const [userName, setUserName] = useState(null);
    useEffect(() => {
        const fetchUserName = async () => {
          try {
            const response = await axios.get('http://localhost:8000/api/useres');
            const user = response.data[0]; // Assuming the first user is the desired user
      
            setUserName(user.name);
          } catch (error) {
            console.error('Error fetching user name:', error);
          }
        };
      
        fetchUserName();
      }, []);


  return (
    <div>
      <h1>Dashboard</h1>
      {/* Add your dashboard content here */}
    </div>
  );
};

export default Dashboard;
