import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/header';
import Body from './components/body';
import Footer from './components/footer';
import Sidebar from './components/Sidebar';
import Dashboard from './components/dashboard';
import Login from './components/login';
import AddProduct from './components/addproduct';
import Update from './components/update';
import MyProducts from './components/myproducts';
import Cart from './components/cart';
import Register from './components/registration';
import axios from 'axios';
import { useLocation } from 'react-router-dom';
import AllProducts from './components/allproducts';


axios.defaults.withCredentials = true;

function App() {

  const location = useLocation();

  // Define an array of paths where the Sidebar should not be rendered
  const excludeSidebarPaths = ['/', '/register', '/login'];

  // Check if the current path is in the array of paths to exclude Sidebar
  const shouldRenderSidebar = !excludeSidebarPaths.includes(location.pathname.toLowerCase());

  return (
    <div>
      <Header />
      {shouldRenderSidebar && (
        <div id="layoutSidenav">
          <div id="layoutSidenav_nav">
            <Sidebar />
          </div>
          <div id="layoutSidenav_content">
            <main>
              <Routes>
                <Route path="/dashboard" element={<Dashboard />} />
                <Route path="/addproduct" element={<AddProduct />} />
                <Route path="/myproducts" element={<MyProducts />} />
                <Route path="/myproducts/update/:id" element={<Update />} />
                <Route path="/cart" element={<Cart />} />
                {/* Add more routes as needed */}
                <Route path="*" element={<Navigate to="/" />} />
              </Routes>
            </main>
          </div>
        </div>
      )}
      <Body />
      <Footer />
    </div>
  );
}

export default App;
