import React from 'react';
import { useEffect, useState } from "react";
import { Nav, NavDropdown } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";


const Dashboard = () => {

    const navigate = useNavigate();
    // const [userName, setUserName] = useState(null);
    // useEffect(() => {
    //     const fetchUserName = async () => {
    //       try {
    //         const response = await axios.get('http://localhost:8000/api/useres');
    //         const user = response.data[0]; // Assuming the first user is the desired user
      
    //         setUserName(user.name);
    //       } catch (error) {
    //         console.error('Error fetching user name:', error);
    //       }
    //     };
      
    //     fetchUserName();
    //   }, []);
    const userName = localStorage.getItem("auth_name");

      function Logout() {
        localStorage.clear();
        navigate("/");
      }

  return (
    <div>
      <h1>Dashboard</h1>
      <div className="header">
      <ul className="navbar">
        <li className="logo">
          <span>SomeLogo</span>
        </li>
          <li>
            <Nav>
              <i className="fa-solid fa-user"></i>
              <NavDropdown title={`User ${userName}`} className="logname">
                <Link to="/logout">
                  <NavDropdown.Item onClick={Logout}>
                    <i className="fa-solid fa-right-from-bracket"></i>Logout
                  </NavDropdown.Item>
                </Link>
              </NavDropdown>
            </Nav>
          </li>
        </ul>

        </div>
        <div className="bodybuttons">
            <Link to="/">
              <button>
                <i className="fa-solid fa-server"></i>All Products
              </button>
            </Link>

            <Link to="/myproducts">
              <button>
                <i className="fa-solid fa-box-open"></i>My Products
              </button>
            </Link>

            <Link to="/addproduct">
              <button>
                <i className="fa-solid fa-plus"></i>Add Product
              </button>
            </Link>

            <Link to="/cart">
              <button>
                <i className="fa-solid fa-shopping-cart"></i>Cart
              </button>
            </Link>
          </div>
          

    </div>
  );
};

export default Dashboard;
