        //   // Parse JSON only if data is available
        //   const parsedData = JSON.parse(response.data);
        //   setUserName(parsedData.name);
        // } else {
        //   console.warn('Response data is empty.');
        // }
   // }


  function Logout() {
    localStorage.clear();
    navigate("/");
  }

  return (
    <div className="header">
      <ul className="navbar">
        <li className="logo">
          <span>SomeLogo</span>
        </li>
        {userName ? (
          <li>
            <Nav>
              <i className="fa-solid fa-user"></i>
              <NavDropdown title={`User ${userName}`} className="logname">
                <Link to="/logout">
                  <NavDropdown.Item onClick={Logout}>
                    <i className="fa-solid fa-right-from-bracket"></i>Logout
                  </NavDropdown.Item>
                </Link>
              </NavDropdown>
            </Nav>
          </li>
        ) : (
          <>
            <li className="forlogin">
              <Link to="/login">
                <div className="logindiv">
                  <i className="fa-solid fa-arrow-right-to-bracket"></i>
                  <span>Login</span>
                </div>
              </Link>
            </li>
            <li className="forreg">
              <Link to="/register">
                <div className="regdiv">
                  <i className="fa-solid fa-user-plus"></i>
                  <span>Register</span>
                </div>
              </Link>
            </li>
          </>
        )}
      </ul>
    </div>
  );
}
