import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import Header from "./header";
import Footer from "./footer";

export default function Update() {
  const navigate = useNavigate();
  const params = useParams();
  const id = params.id;

  const [productData, setProductData] = useState({
    product: "",
    manufacturer: "",
    price: "",
    quantity: "",
    tags: "",
    pdimg: "",
    description: "",
  });

  useEffect(() => {
    const fetchProductData = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:8000/api/getproduct/${id}`);
        const data = await response.json();
        setProductData(data);
      } catch (error) {
        console.error("Error fetching product data:", error);
      }
    };

    fetchProductData();
  }, [id]);

  const handleInputChange = (event, field) => {
    setProductData({
      ...productData,
      [field]: event.target.value,
    });
  };

  const handleChanges = async (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("product", productData.product);
    formData.append("manufacturer", productData.manufacturer);
    formData.append("price", productData.price);
    formData.append("quantity", productData.quantity);
    formData.append("tags", productData.tags);
    formData.append("pdimg", productData.pdimg);
    formData.append("description", productData.description);

    try {
      const result = await fetch(`http://127.0.0.1:8000/api/updateproduct/${id}?_method=PUT`, {
        method: "POST",
        body: formData,
      });

      if (result.ok) {
        alert("Product Updated");
        navigate("/myproducts");
      } else {
        console.error("Error updating product");
      }
    } catch (error) {
      console.error("Error updating product:", error);
    }
  };

  if (localStorage.getItem("user")) {
    return (
      <div>
        <Header />
        <div className="formbody">
          <div className="addproductheader">
            <div className="togoback">
              <a href="/myproducts">Go Back</a>
            </div>
          </div>
          <div className="formdiv">
            <form onSubmit={handleChanges}>
              {/* ... (rest of your form inputs) */}

              <div>
                <label htmlFor="description">Product Description</label>
                <textarea
                  name="description"
                  rows="10"
                  value={productData.description}
                  onChange={(event) => handleInputChange(event, "description")}
                  placeholder="Include product's details"
                ></textarea>
              </div>

              <button type="submit">Update Product</button>
            </form>
          </div>
        </div>
        <Footer />
      </div>
    );
  } else {
    useEffect(() => {
      navigate("/login");
    }, [navigate]);
    return null;
  }
}
