import React, { useState } from "react";

export default function Contact() {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });

  const [formErrors, setFormErrors] = useState({
    name: "",
    email: "",
    message: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  const validateForm = () => {
    const errors = {};
    if (!formData.name) {
      errors.name = "Name is required";
    }
    if (!formData.email) {
      errors.email = "Email is required";
    }
    if (!formData.message) {
      errors.message = "Message is required";
    }
    setFormErrors(errors);
    return Object.keys(errors).length === 0; // Return true if there are no errors
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // Form is valid, you can submit the data or perform other actions
      alert("Your message has been sent");
      console.log(formData);
    } else {
      // Form is not valid, you can display an error message or take other actions
    }
  };

  return (
  <section class="mb-4">

      
      <h2 class="h1-responsive font-weight-bold text-center my-4">Contact us</h2>
      
      <p class="text-center w-responsive mx-auto mb-5">Do you have any questions? Please do not hesitate to contact us directly. Our team will come back to you within
          a matter of hours to help you.</p>

      <div class="row">

          
          <div class="col-md-9 mb-md-0 mb-5">
              
              <form id="contact-form" name="contact-form" action="mail.php" method="POST" onSubmit={handleSubmit}>

                  
                  <div class="row">

                      
                      <div class="col-md-6">
                          <div class="md-form mb-0">

                              <input type="text" id="name" name="name" class="form-control" onChange={handleInputChange} value={formData.name} />
                              <span style={{ color: "red" }}>{formErrors.name}</span>
                              
                              <label for="name" class="">Your name</label>
                          </div>
                      </div>
                      
                      <div class="col-md-6">
                          <div class="md-form mb-0">
                            <input type="text" id="email" name="email" class="form-control" onChange={handleInputChange} value={formData.email} />
                            <label for="email" class="">Your email</label>
                          </div>
                      </div>
                

                  </div>
                  
                  <div class="row">
                      <div class="col-md-12">
                          <div class="md-form mb-0">
                              <input type="text" id="message" class="form-control" onChange={handleInputChange} value={formData.message} />
                              <label for="message" class="">Subject</label>
                          </div>
                      </div>
                  </div>
                  
                  <div class="row">

                      
                      <div class="col-md-12">

                          <div class="md-form">
                              <textarea type="text" id="message" name="message" rows="2" class="form-control md-textarea"></textarea>
                              <label for="message">Your message</label>
                          </div>

                      </div>
                  </div>

              </form>

              <div class="text-center text-md-left">
                  <a class="btn btn-primary" onclick="document.getElementById('contact-form').submit();">Send</a>
              </div>
              <div class="status"></div>
          </div>
          <div class="col-md-3 text-center">
              <ul class="list-unstyled mb-0">
                  <li><i class="fas fa-map-marker-alt fa-2x"></i>
                      <p>San Francisco, CA 94126, USA</p>
                  </li>

                  <li><i class="fas fa-phone mt-4 fa-2x"></i>
                      <p>+ 01 234 567 89</p>
                  </li>

                  <li><i class="fas fa-envelope mt-4 fa-2x"></i>
                      <p>contact@mdbootstrap.com</p>
                  </li>
              </ul>
          </div>

      </div>

  </section>

  );
}
