import React, { useState } from 'react';
import axios from 'axios';
import Swal from 'sweetalert2';
import { Link } from 'react-router-dom';
import Headers from './header.js';

function AddProduct(){
    const [productInput, setProduct] = useState({
		product: "",
		manufacturer: "",
		price: "",
		quantity: "",
		tags: "",
		pdimg: "",
		description: "",
		user_id: "",
		error_list: [],
    });
    const [picture,setPicture] = useState([]);

    const handleInput = (e) => {
        e.persist();
        setProduct({...productInput, [e.target.name]: e.target.value});
    }
    const handleImage = (e) =>{
        setPicture({pdimg: e.target.files[0]});
    }

    const submitProduct =(e) => {
        e.preventDefault();
        const formData = new FormData();
		formData.append("user_id", productInput.user_id);
		formData.append("product", productInput.product);
		formData.append("manufacturer", productInput.manufacturer);
		formData.append("price", productInput.price);
		formData.append("quantity", productInput.quantity);
		formData.append("tags", productInput.tags);
		formData.append("pdimg", picture.pdimg);
		formData.append("description", productInput.description);


        axios.post(`/api/addproduct`,formData).then(res => {
            if(res.data.status === 200)
            {
                Swal.fire("Success",res.data.message,"success");
                document.getElementById('PRODUCT_FORM').reset();
            }
            else if(res.data.status === 422)
            {
                setProduct({...productInput, error_list:res.data.validation_errors});
            }
        });
    }
    return(
        <><Headers /><div className='container-fluid px-4'>
            <h1 className='mt-4'>Add Product</h1>

            <form onSubmit={submitProduct} id="PRODUCT_FORM">
                <ul className="nav nav-tabs" id="myTab" role="tablist">
                    <li className="nav-item" role="presentation">
                        <Link className="nav-link active" id="home-tab" data-bs-toggle="tab" to="/" role="tab" aria-controls="home" aria-selected="true">Home</Link>
                    </li>
                    {/* <li className="nav-item" role="presentation">
        <button className="nav-link" id="pro-tag-tab" data-bs-toggle="tab" data-bs-target="#pro-tag" type="button" role="tab" aria-controls="profile" aria-selected="false">SEO Tag</button>
    </li> */}
                </ul>
                <div className="tab-content" id="myTabContent">
                    <div className="tab-pane fade show active" id="home" role="tabpanel" aria-labelledby="home-tab">
                        <div className='form-group mb-3'>
                            <label>Product Name</label>
                            <input type={"text"} name='product' onChange={handleInput} value={productInput.product} className='form-control' />
                            <span>{productInput.error_list.product}</span>

                        </div>

                        <div className='form-group mb-3'>
                            <label>Manufacturer</label>
                            <input type={"text"} name='manufacturer' onChange={handleInput} value={productInput.manufacturer} className='form-control' />
                            <span>{productInput.error_list.manufacturer}</span>
                        </div>

                        <div className='form-group mb-3'>
                            <label>Price</label>
                            <input type={"text"} name='price' onChange={handleInput} value={productInput.price} className='form-control' />
                            <span>{productInput.error_list.price}</span>

                        </div>

                        <div className='form-group mb-3'>
                            <label>Quantity</label>
                            <input type={"text"} name='quantity' onChange={handleInput} value={productInput.quantity} className='form-control' />
                            <span>{productInput.error_list.quantity}</span>

                        </div>

                        <div className='form-group mb-3'>
                            <label>Tags (Comma Separated)</label>
                            <input type={"text"} name='tags' onChange={handleInput} value={productInput.tags} className='form-control' />
                            <span>{productInput.error_list.tags}</span>

                        </div>
                        <div className='form-group mb-3'>
                            <label>Image</label>
                            <input type={"file"} name='pdimg' onChange={handleImage} className='form-control' />
                            {/* <span>{picture.image}</span> */}

                        </div>

                        <div className='form-group mb-3'>
                            <label>Product Description</label>
                            <input type={"text"} name='description' onChange={handleInput} value={productInput.description} className='form-control' />
                            <span>{productInput.error_list.description}</span>

                        </div>
                    </div>

                </div>
                <button type="submit" className="btn btn-primary px-4">Submit</button>
            </form>
        </div></>
    )
}
export default AddProduct;
